import * as React from "react";
import { connect } from "react-redux";
import { Button } from 'react-bootstrap';
import { logOut, updateAccount } from "../actions/current";
import { ICurrent, AUser } from "../types";
import { Link } from "react-router-dom";
import { useSelector, shallowEqual } from "react-redux";

interface IProps {
  logOutConnect: () => void;
  accountUpdate: (user: AUser | any) => void;
  state: ICurrent
}

//{name: string, email: string, password: string, identity: string}
const Account = ({ logOutConnect, accountUpdate, state }: IProps) => (
  <>
    <div className="container">
      <div className="row align-items-center">
        <div className="col-md-4 mx-auto">
          <div className="text-center bbox-title border" >
            <h4>Account</h4>
            <small>Below your details, you can update.</small>
          </div>
          <div className="bbox-box border">
            <form onSubmit={accountUpdate} >
              <div className="form-group">
                <label className="form-label" >Full Name</label>
                <input type="text" name="name" id="name" defaultValue={state.user.name} className="form-control" />
              </div>
              <div className="form-group">
                <label className="form-label" >Email address</label>
                <input type="email" name="email" id="email" defaultValue={state.user.email} className="form-control" />
              </div>
              <div className="form-group">
                <label className="form-label" >Password</label>
                <input type="password" name="password" id="password" defaultValue={state.user.password} className="form-control" />
              </div>
              <div className="form-group">
                <label className="form-label" >Passport / Identity Card </label>
                <div className="form-file">
                  <input id="identity" type="file" name="identity" defaultValue={state.user.identity} className="form-control-file" />
                </div>
              </div>
              <button type="submit" className="btn btn-success">Update</button>
              <Button variant="danger" type="button" onClick={logOutConnect} className="float-right" >Logout</Button>
            </form>
          </div>
          <div className="container" >
            <Link to="/" >{'< '} Home </Link>
          </div>
        </div>
      </div>
    </div>
  </>
);

const mapStateToProps = (state: ICurrent) => ({
  state
});

const mapDispatchToProps = {
  logOutConnect: logOut,
  accountUpdate: updateAccount
};

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(Account);
